import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { AiOutlineDelete, AiOutlineEdit } from "react-icons/ai";

const admin = localStorage.getItem("admin");

function ProductComponent() {
  const products = useSelector((state) => state.allproducts.products);
  console.log(products);

  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const handleDelete = (productId) => {
    setShowDeleteConfirmation(true);
    setSelectedProductId(productId);
  };

  const handleConfirmDelete = () => {

    setShowDeleteConfirmation(false);
  };

  const handleCancelDelete = () => {
    setShowDeleteConfirmation(false);
  };

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredProducts = products.filter((product) => {
    return product.title.toLowerCase().includes(searchQuery.toLowerCase());
  });

  const renderList = filteredProducts.map((product) => {
    const { _id, title, image, price, category } = product;
    return (
      <div className="col-md-3 py-4 mt-10" key={_id}>
        <div className="card">
          <Link to={`/product/${_id}`}>
            <img className="card-img-top" src={`${image}`} alt={title} />
          </Link>
          <div>
            <h5 className="text-[2rem] text-center my-4">{title}</h5>
            <h5 className="card-subtitle text-center"> {price} PKR</h5>
            <h6 className="card-subtitle text-center text-muted my-2">
              {category}
            </h6>
          </div>
          {admin ? (
            <div className="flex my-2 gap-2">
              <button
                className="btn px-4 bg-red-500 rounded"
                onClick={() => handleDelete(_id)}
              >
                <AiOutlineDelete />
              </button>
              <button className="btn px-4 bg-yellow-500 rounded">
                <AiOutlineEdit />
              </button>
            </div>
          ) : (
            <button className="btn bg-green-300">Buy Now</button>
          )}
        </div>
      </div>
    );
  });

  return (
    <>
      <div className="my-4 w-full mt-48 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          className="border border-gray-300 rounded px-4 py-2"
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>
      <div className="flex flex-wrap">{renderList}</div>
      {showDeleteConfirmation && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-md">
            <h3 className="text-xl font-bold mb-4">Confirm Deletion</h3>
            <p className="text-gray-600 mb-4">
              Are you sure you want to delete this product?
            </p>
            <div className="flex justify-end">
              <button
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded mr-2"
                onClick={handleConfirmDelete}
              >
                Delete
              </button>
              <button
                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-4 py-2 rounded"
                onClick={handleCancelDelete}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default ProductComponent;
