import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { AiOutlineDelete, AiOutlineEdit } from "react-icons/ai";

const admin = localStorage.getItem("admin");

function ProductComponent() {
  const products = useSelector((state) => state.allproducts.products);
  console.log(products);

  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [selectedProductId, setSelectedProductId] = useState(null);

  const handleDelete = (productId) => {
    setShowDeleteConfirmation(true);
    setSelectedProductId(productId);
  };

  const handleConfirmDelete = () => {
    // Perform the delete operation using the selectedProductId
    // ...
    // After the delete operation, you can close the confirmation popup
    setShowDeleteConfirmation(false);
  };

  const handleCancelDelete = () => {
    setShowDeleteConfirmation(false);
  };

  const renderList = products.map((product) => {
    const { _id, title, image, price, category } = product;
    return (
      <Link to={`/product/${_id}`} key={_id}>
        <div className="col-md-3 py-4 mt-48">
          <div className="card">
            <img className="card-img-top" src={`${image}`} alt={title} />
            <div>
              <h5 className="text-[2rem] text-center my-4">{title}</h5>
              <h5 className="card-subtitle text-center"> {price} PKR</h5>
              <h6 className="card-subtitle text-center text-muted my-2">
                {category}
              </h6>
            </div>
            {admin ? (
              <div className="flex my-2 gap-2">
                <button
                  className="btn px-4 bg-red-500 rounded"
                  onClick={() => handleDelete(_id)}
                >
                  <AiOutlineDelete />
                </button>
                <button className="btn px-4 bg-yellow-500 rounded">
                  <AiOutlineEdit />
                </button>
              </div>
            ) : (
              <button className="btn bg-green-300">Buy Now</button>
            )}
          </div>
        </div>
      </Link>
    );
  });

  return (
    <>
      {renderList}
      {showDeleteConfirmation && (
        <div className="delete-confirmation">
          <h3>Confirm Deletion</h3>
          <p>Are you sure you want to delete this product?</p>
          <button onClick={handleConfirmDelete}>Delete</button>
          <button onClick={handleCancelDelete}>Cancel</button>
        </div>
      )}
    </>
  );
}

export default ProductComponent;
