import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { AiFillDelete } from "react-icons/ai";
import { useDispatch, useSelector } from "react-redux";
import { setOrders } from "../Redux/Actions/orderActions";
import { fetchOrders } from "../api";

const admin = localStorage.getItem("admin");

function Orders() {
    if (!admin) return <div className="pt-48 pl-4">You are not authorized</div>;

    const orders = useSelector((state) => state.allOrders.orders);
    const dispatch = useDispatch();

    async function fetchProducts() {
        try {
            const response = await fetchOrders();
            dispatch(setOrders(response.data.data));
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    console.log(orders);

    return (
        <div className="px-8 flex flex-col mx-auto py-8 pt-48">
            <h2 className="text-2xl font-bold mb-4 min-w-[100%]">Orders</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {orders.map(
                    ({
                        _id,
                        productLink,
                        customerName,
                        customerNumber,
                        address,
                        price,
                    }) => (
                        <div
                            className="bg-white shadow border min-w-[290px] rounded p-4 relative"
                            key={_id}
                        >
                            <p>
                                Product:{" "}
                                <Link
                                    to={productLink}
                                    className="text-blue-500"
                                >
                                    View Product
                                </Link>
                            </p>
                            <p className="text-gray-600">
                                Customer Name: {customerName}
                            </p>
                            <p className="text-gray-600">
                                Customer Number: {customerNumber}
                            </p>
                            <p className="text-gray-600">
                                Customer Address: {address}
                            </p>
                            <p className="text-gray-600">Price: {price}</p>
                            <div className="absolute bottom-4 right-4">
                                <button className="text-red-500">
                                    <AiFillDelete size={24} />
                                </button>
                            </div>
                        </div>
                    )
                )}
            </div>
        </div>
    );
}

export default Orders;
