import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import {
    selectedProduct,
    removeSelectedProduct,
} from "../Redux/Actions/productActions";

function CreateOrder() {
    const product = useSelector((state) => state.allproducts.product);
    const { title, price, image, category, desc } = product;
    const { productId } = useParams();
    const dispatch = useDispatch();

    const [customerName, setCustomerName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [area, setArea] = useState("");
    const [address, setAddress] = useState("");

    console.log(product);

    async function fetchProductDetail() {
        const response = await axios
            .get(`http://localhost:5000/products/${productId}`)
            .catch((err) => {
                console.log(err);
            });

        dispatch(selectedProduct(response.data));
    }

    useEffect(() => {
        if (productId && productId !== "") {
            fetchProductDetail();
        }

        return () => {
            dispatch(removeSelectedProduct());
        };
    }, [productId]);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Perform your form submission logic here
        // You can access the form input values from the state variables
        console.log({
            productName: title,
            productImage: image,
            productPrice: price,
            customerName,
            phoneNumber,
            area,
            address,
        });
    };

    return (
        <div className="detail">
            {Object.keys(product).length === 0 ? (
                <div className="mt-48">...loading</div>
            ) : (
                <div className="container mx-auto py-8">
                    <h2 className="text-3xl font-bold mb-6 text-gray-800">
                        Create Order
                    </h2>
                    <div className="mb-6">
                        <label
                            htmlFor="productName"
                            className="block text-lg font-medium text-gray-700 mb-2"
                        >
                            Product Name
                        </label>
                        <p className="text-xl text-gray-800">{title}</p>
                    </div>
                    <div className="mb-6">
                        <label
                            htmlFor="productImage"
                            className="block text-lg font-medium text-gray-700 mb-2"
                        >
                            Product Image
                        </label>
                        <img
                            src={image}
                            alt={title}
                            className="w-64 h-auto rounded-lg"
                        />
                    </div>
                    <div className="mb-6">
                        <label
                            htmlFor="productPrice"
                            className="block text-lg font-medium text-gray-700 mb-2"
                        >
                            Product Price
                        </label>
                        <p className="text-xl text-gray-800">{price}</p>
                    </div>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-6">
                            <label
                                htmlFor="customerName"
                                className="block text-lg font-medium text-gray-700 mb-2"
                            >
                                Customer Name
                            </label>
                            <input
                                type="text"
                                id="customerName"
                                value={customerName}
                                onChange={(e) =>
                                    setCustomerName(e.target.value)
                                }
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            />
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="phoneNumber"
                                className="block text-lg font-medium text-gray-700 mb-2"
                            >
                                Phone Number
                            </label>
                            <input
                                type="text"
                                id="phoneNumber"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            />
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="area"
                                className="block text-lg font-medium text-gray-700 mb-2"
                            >
                                Area
                            </label>
                            <select
                                id="area"
                                value={area}
                                onChange={(e) => setArea(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            >
                                <option value="">Select an area</option>
                                <option value="Area A">Area A</option>
                                <option value="Area B">Area B</option>
                                <option value="Area C">Area C</option>
                            </select>
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="address"
                                className="block text-lg font-medium text-gray-700 mb-2"
                            >
                                Address
                            </label>
                            <textarea
                                id="address"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full resize-none focus:outline-none focus:border-blue-500"
                                required
                            ></textarea>
                        </div>
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
                        >
                            Create Order
                        </button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default CreateOrder;
