import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import {
    selectedProduct,
    removeSelectedProduct,
} from "../Redux/Actions/productActions";
import { create_order } from "../Redux/Actions/orderActions";

const areas = [
    "Clifton",
    "Defence (DHA - Defence Housing Authority)",
    "Gulshan-e-Iqbal",
    "Gulistan-e-Johar",
    "Saddar",
    "Tariq Road",
    "PECHS (Pakistan Employees Cooperative Housing Society)",
    "North Nazimabad",
    "Nazimabad",
    "Gulshan-e-Maymar",
    "Malir",
    "Shah Faisal Colony",
    "Landhi",
    "Korangi",
    "Keamari",
    "Lyari",
    "Federal B. Area",
    "Orangi Town",
    "Liaquatabad",
    "Gulberg",
];

function OrderSuccessPopup({ onClose }) {
    return (
        <div className="fixed top-0 left-0 w-full h-full flex items-center justify-center bg-gray-800 bg-opacity-50">
            <div className="bg-white rounded-lg p-8">
                <h2 className="text-2xl font-bold mb-4">
                    Order Created Successfully
                </h2>
                <p>
                    Your order has been placed successfully. You will receive
                    the product soon.
                </p>
                <div className="flex justify-end mt-6">
                    <button
                        onClick={onClose}
                        className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
                    >
                        Continue Shopping
                    </button>
                </div>
            </div>
        </div>
    );
}

function CreateOrder() {
    const navigate = useNavigate();
    const product = useSelector((state) => state.allproducts.product);
    const { title, price, image, category, desc } = product;
    const { productId } = useParams();
    const dispatch = useDispatch();

    const [customerName, setCustomerName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [area, setArea] = useState("");
    const [address, setAddress] = useState("");
    const [showPopup, setShowPopup] = useState(false);

    async function fetchProductDetail() {
        const response = await axios
            .get(`http://localhost:5000/products/${productId}`)
            .catch((err) => {
                console.log(err);
            });

        dispatch(selectedProduct(response.data));
    }

    useEffect(() => {
        if (productId && productId !== "") {
            fetchProductDetail();
        }

        return () => {
            dispatch(removeSelectedProduct());
        };
    }, [productId]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const order = {
            productLink: `http://localhost:3000/product/${productId}`,
            customerName,
            customerNumber: phoneNumber,
            address,
            price: totalPrice,
        };
        dispatch(create_order(order, navigate));
        setShowPopup(true);
    };

    const handlePopupClose = () => {
        setShowPopup(false);
        navigate("/"); // Redirect to home or desired page
    };

    return (
        <div className="detail">
            {Object.keys(product).length === 0 ? (
                <div className="mt-48">...loading</div>
            ) : (
                <div className="container mx-auto py-8 pt-48 flex flex-col items-center">
                    <h2 className="text-5xl font-bold mb-6 text-gray-800">
                        Create Order
                    </h2>
                    <div className="mb-6">
                        <p className="text-3xl text-gray-800">{title}</p>
                    </div>
                    <div className="mb-6">
                        <img
                            src={image}
                            alt={title}
                            className="w-64 h-auto rounded-lg"
                        />
                    </div>

                    <form
                        onSubmit={handleSubmit}
                        className="w-[300px] sm:w-[600px]"
                    >
                        <div className="mb-6">
                            <label
                                htmlFor="customerName"
                                className="block text-xl font-medium text-gray-900 mb-2"
                            >
                                Name
                            </label>
                            <input
                                type="text"
                                id="customerName"
                                value={customerName}
                                onChange={(e) =>
                                    setCustomerName(e.target.value)
                                }
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            />
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="phoneNumber"
                                className="block text-xl font-medium text-gray-900 mb-2"
                            >
                                Phone Number
                            </label>
                            <input
                                type="text"
                                id="phoneNumber"
                                value={phoneNumber}
                                onChange={(e) => setPhoneNumber(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            />
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="area"
                                className="block text-xl font-medium text-gray-900 mb-2"
                            >
                                Area
                            </label>
                            <select
                                id="area"
                                value={area}
                                onChange={(e) => setArea(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full focus:outline-none focus:border-blue-500"
                                required
                            >
                                <option value="">Select an area</option>
                                <option value="Gulistan e Johar">
                                    Gulistan e Johar
                                </option>
                                <option value="Gulshan Iqbal">
                                    Gulshan Iqbal
                                </option>
                                <option value="Gulshan Iqbal">Saddar</option>
                            </select>
                        </div>
                        <div className="mb-6">
                            <label
                                htmlFor="address"
                                className="block text-xl font-medium text-gray-900 mb-2"
                            >
                                Address
                            </label>
                            <textarea
                                id="address"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                                className="border border-gray-300 rounded-lg px-4 py-3 w-full resize-none focus:outline-none focus:border-blue-500"
                                required
                            ></textarea>
                        </div>
                        <div className="mb-6 font-semibold">
                            <p>Product Price: {price} PKR</p>
                        </div>
                        <div className="mb-6 font-semibold">
                            <p>Delivery fees: 100 PKR</p>
                        </div>
                        <div className="mb-6 font-semibold">
                            <p>Total Price: {price + 100} PKR</p>
                        </div>
                        <div className="mb-6 font-semibold">
                            <p>Payment type: Cash on Delivery</p>
                        </div>

                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-6 py-3 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
                        >
                            Confirm Order
                        </button>
                    </form>
                    {showPopup && (
                        <OrderSuccessPopup onClose={handlePopupClose} />
                    )}
                </div>
            )}
        </div>
    );
}

export default CreateOrder;
