import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { AiFillDelete } from "react-icons/ai";

function ProductComponent() {
    const products = useSelector((state) => state.allproducts.products);
    console.log(products);

    const renderList = products.map((product) => {
        const { _id, title, image, price, category } = product;
        return (
            <Link to={`/product/${_id}`} key={_id}>
                <div className="col-md-3 py-4 mt-48">
                    <div className="card">
                        <img
                            className="card-img-top"
                            src={`${image}`}
                            alt={title}
                        />
                        <div>
                            <h5 className="text-[2rem] text-center my-4">
                                {" "}
                                {title}{" "}
                            </h5>
                            <h5 className="card-subtitle"> {price} PKR</h5>
                            <h6 className="card-subtitle text-muted my-2">
                                {" "}
                                {category}{" "}
                            </h6>
                        </div>
                        <div className="flex">
                            <button className="btn bg-green-300 my-2">
                                Buy Now
                            </button>
                            <button className="px-4 bg-red-500 rounded">
                                <AiFillDelete />
                            </button>
                        </div>
                    </div>
                </div>
            </Link>
        );
    });

    return <>{renderList}</>;
}

export default ProductComponent;
