import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link, useNavigate } from "react-router-dom";
import { AiOutlineDelete, AiOutlineEdit } from "react-icons/ai";
import { useDispatch } from "react-redux";
import { deleteProduct } from "../Redux/Actions/productActions";
import Box from "@mui/material/Box";
import SwipeableDrawer from "@mui/material/SwipeableDrawer";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import Divider from "@mui/material/Divider";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";

const admin = JSON.parse(localStorage.getItem("admin"));

function ProductComponent() {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const products = useSelector((state) => state.allproducts.products);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
    const [selectedProductId, setSelectedProductId] = useState(null);
    const [searchQuery, setSearchQuery] = useState("");
    const [left, setLeft] = useState(false);

    const toggleDrawer = (anchor, open) => (event) => {
        if (
            event &&
            event.type === "keydown" &&
            (event.key === "Tab" || event.key === "Shift")
        ) {
            return;
        }

        setLeft(open);
    };

    const list = (anchor) => (
        <Box
            sx={{
                width: anchor === "top" || anchor === "bottom" ? "auto" : 250,
            }}
            role="presentation"
            onClick={toggleDrawer(anchor, false)}
            onKeyDown={toggleDrawer(anchor, false)}
        >
            <List>
                {categories.map(
                    (text, index) => (
                        <ListItem key={text} disablePadding>
                            <ListItemButton>
                                <ListItemText primary={text} />
                            </ListItemButton>
                        </ListItem>
                    )
                )}
            </List>
            <Divider />
            <List>
                {["All mail", "Trash", "Spam"].map((text, index) => (
                    <ListItem key={text} disablePadding>
                        <ListItemButton>
                            <ListItemIcon>
                                {index % 2 === 0 ? <InboxIcon /> : <MailIcon />}
                            </ListItemIcon>
                            <ListItemText primary={text} />
                        </ListItemButton>
                    </ListItem>
                ))}
            </List>
        </Box>
    );

    const handleDelete = (productId) => {
        setShowDeleteConfirmation(true);
        setSelectedProductId(productId);
    };

    const handleConfirmDelete = () => {
        dispatch(deleteProduct(selectedProductId, navigate));
        setShowDeleteConfirmation(false);
    };

    const handleCancelDelete = () => {
        setShowDeleteConfirmation(false);
    };

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleCategorySelect = (category) => {
        setSelectedCategory(category);
    };

    const filteredProducts = products.filter((product) => {
        return (
            product.title.toLowerCase().includes(searchQuery.toLowerCase()) &&
            (!selectedCategory || product.category === selectedCategory)
        );
    });

    const categories = ["Electronics", "Clothing", "Books", "Accessories"];

    const renderList = filteredProducts.map((product) => {
        const { _id, title, images, price, category } = product;
        return (
            <div className=" py-4 px-4 mt-10 w-[250px]" key={_id}>
                <div className="card">
                    <Link to={`/product/${_id}`}>
                        <img
                            className="card-img-top"
                            src={`${images[0]}`}
                            alt={title}
                        />
                    </Link>
                    <div>
                        <h5 className="text-[2rem] text-center my-4">
                            {title}
                        </h5>
                        <h5 className="card-subtitle text-center">
                            {" "}
                            {price} PKR
                        </h5>
                        <h6 className="card-subtitle text-center text-muted my-2">
                            {category}
                        </h6>
                    </div>
                    {admin ? (
                        <div className="flex my-2 gap-2">
                            <button
                                className="btn px-4 bg-red-500 rounded"
                                onClick={() => handleDelete(_id)}
                            >
                                <AiOutlineDelete />
                            </button>
                            <button
                                className="btn px-4 bg-yellow-500 rounded"
                                onClick={() => navigate(`/edit/${_id}`)}
                            >
                                <AiOutlineEdit />
                            </button>
                        </div>
                    ) : (
                        <button
                            className="btn bg-green-300"
                            onClick={() => navigate(`/create-order/${_id}`)}
                        >
                            Buy Now
                        </button>
                    )}
                </div>
            </div>
        );
    });

    return (
        <>
            <div className="my-4 w-full mt-48 flex justify-center">
                <input
                    type="text"
                    placeholder="Search products..."
                    className="border border-gray-300 rounded px-4 py-2"
                    value={searchQuery}
                    onChange={handleSearch}
                />
            </div>
            <div className="sm:px-8 w-full md:w-1/4 mx-auto rounded bg-gray-100 p-4 flex gap-6 sm:gap-14 items-center">
                <h3 className="text-3xl font-bold ">Categories</h3>
                <ul className="flex gap-4 sm:gap-8 text-3xl">
                    <li
                        className={`cursor-pointer ${
                            selectedCategory === null
                                ? "font-bold text-blue-500"
                                : ""
                        }`}
                        onClick={() => handleCategorySelect(null)}
                    >
                        All
                    </li>
                    {categories.map((category) => (
                        <li
                            key={category}
                            className={`cursor-pointer ${
                                selectedCategory === category
                                    ? "font-bold text-blue-500"
                                    : ""
                            }`}
                            onClick={() => handleCategorySelect(category)}
                        >
                            {category}
                        </li>
                    ))}
                </ul>
            </div>

            {/* PRODUCT LIST  */}
            <div className="flex flex-wrap">{renderList}</div>
            {showDeleteConfirmation && (
                <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
                    <div className="bg-white p-6 rounded shadow-md">
                        <h3 className="text-xl font-bold mb-4">
                            Confirm Deletion
                        </h3>
                        <p className="text-gray-600 mb-4">
                            Are you sure you want to delete this product?
                        </p>
                        <div className="flex justify-end">
                            <button
                                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded mr-2"
                                onClick={handleConfirmDelete}
                            >
                                Delete
                            </button>
                            <button
                                className="bg-gray-300 hover:bg-gray-400 text-gray-700 px-4 py-2 rounded"
                                onClick={handleCancelDelete}
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
}

export default ProductComponent;
