import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { AiFillDelete } from "react-icons/ai";
import { useDispatch, useSelector } from "react-redux";
import { setOrders } from "../Redux/Actions/orderActions";
import axios from "axios";

function Orders() {
    const orders = useSelector((state) => state.allOrders.orders);
    const dispatch = useDispatch();

    async function fetchProducts() {
        const response = await axios
            .get("http://localhost:5000/orders")
            .catch((err) => {
                console.log("Err", err);
            });
        dispatch(setOrders(response.data.data));
    }

    useEffect(() => {
        fetchProducts();
    }, []);

    console.log(orders);

    // const orders = [
    //     {
    //         orderId: 1,
    //         productLink: "/product/1",
    //         customerName: "Waxeem mirza",
    //         customerNumber: "1234567890",
    //         customerLocation: "Noman Grand city",
    //         price: "300",
    //     },
    //     {
    //         orderId: 2,
    //         productLink: "/product/2",
    //         customerName: "Abdullah mirza",
    //         customerNumber: "9876543210",
    //         customerLocation: "Noman grand city",
    //         price: "100",
    //     },
    // ];

    return (
        <div className="container mx-auto py-8 pt-48">
            <h2 className="text-2xl font-bold mb-4">Orders</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {orders.map(
                    ({
                        _id,
                        productLink,
                        customerName,
                        customerNumber,
                        address,
                        price,
                    }) => (
                        <div
                            className="bg-white shadow border rounded p-4 relative"
                            key={_id}
                        >
                            <h3 className="text-lg font-bold mb-2">
                                Order ID: {orderId}
                            </h3>
                            <p>
                                Product:{" "}
                                <Link
                                    to={productLink}
                                    className="text-blue-500"
                                >
                                    View Product
                                </Link>
                            </p>
                            <p className="text-gray-600">
                                Customer Name: {customerName}
                            </p>
                            <p className="text-gray-600">
                                Customer Number: {customerNumber}
                            </p>
                            <p className="text-gray-600">
                                Customer Address: {address}
                            </p>
                            <p className="text-gray-600">Price: {price}</p>
                            <div className="absolute bottom-4 right-4">
                                <button className="text-red-500">
                                    <AiFillDelete size={24} />
                                </button>
                            </div>
                        </div>
                    )
                )}
            </div>
        </div>
    );
}

export default Orders;
