import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom'
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { selectedProduct, removeSelectedProduct } from '../Redux/Actions/productActions';

// const admin = localStorage.getItem("admin");

function Edit() {
    // if (!admin) return <div className="pt-48 pl-4">You are not authorized</div>;

    const product = useSelector(state => state.allproducts.product)
    const { title, price, image, category, description } = product
    const { productId } = useParams();
    const dispatch = useDispatch();

    console.log(product)

    async function fetchProductDetail() {
        const response = await axios.get(`http://localhost:5000/products/${productId}`)
            .catch((err) => {
                console.log(err);
            })

        dispatch(selectedProduct(response.data))
    }

    useEffect(() => {
        if (productId && productId !== "") {
            fetchProductDetail()
        }

        return (() => {
            dispatch(removeSelectedProduct())
        })
    }, [productId])

    return (
        <div className='detail'>
            {Object.keys(product).length === 0 ? (
                <div className='mt-48'>...loading</div>
            ) : (
                <div className='container mt-48'>
                    <div className='image'>
                        <img src={`${image}`} />
                    </div>
                    <div className='text'>
                        <h1>{title}</h1>
                        <div className='price'>
                            <p>{price} PKR</p>
                        </div>
                        <div className="category">
                            <p>{category}</p>
                        </div>
                        <p>
                            {description}
                        </p>
                        <button type="button" className="btn bg-green-300 btn-lg focus:outline-none focus:border-none">Buy Now</button>
                    </div>
                </div>
            )
            }
        </div>
    )
}

export default Edit
