import React, { useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { AiOutlineDelete, AiOutlineEdit } from "react-icons/ai";
import {
    selectedProduct,
    removeSelectedProduct,
} from "../Redux/Actions/productActions";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";

const admin = JSON.parse(localStorage.getItem("admin"));

function ProductDetail() {
    const product = useSelector((state) => state.allproducts.product);
    const { title, price, images, category, desc } = product;
    const { productId } = useParams();
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const handleDelete = (productId) => {
        setShowDeleteConfirmation(true);
        setSelectedProductId(productId);
    };

    const handleConfirmDelete = () => {
        dispatch(deleteProduct(selectedProductId, navigate));
        setShowDeleteConfirmation(false);
    };

    const handleCancelDelete = () => {
        setShowDeleteConfirmation(false);
    };

    async function fetchProductDetail() {
        const response = await axios
            .get(`http://localhost:5000/products/${productId}`)
            .catch((err) => {
                console.log(err);
            });

        dispatch(selectedProduct(response.data));
    }

    useEffect(() => {
        if (productId && productId !== "") {
            fetchProductDetail();
        }

        return () => {
            dispatch(removeSelectedProduct());
        };
    }, [productId]);

    return (
        <div className="detail flex flex-col">
            {Object.keys(product).length === 0 ? (
                <div className="mt-48">...loading</div>
            ) : (
                <div className="container flex justify-center mt-48">
                    <Carousel className="max-w-[200px] sm:max-w-[500px] lg:max-w-[700px] mx-auto">
                        {images.map((image) => (
                            <img
                                src={image}
                                alt="image"
                                className="h-full w-full object-contain max-w-[180px] lg:max-w-[220px]"
                                key={image}
                            />
                        ))}
                    </Carousel>

                    <div className="text lg:mt-32">
                        <h1 className="text-4xl lg:text-6xl font-bold mb-4">
                            {title}
                        </h1>
                        <div className="price">
                            <p className="text-2xl lg:text-4xl font-semibold">
                                {price} PKR
                            </p>
                        </div>
                        <div className="category">
                            <p className="text-gray-500 text-2xl lg:text-3xl">
                                {category}
                            </p>
                        </div>
                        <p className="text-xl text-gray-700 mb-4">{desc}</p>
                        {admin ? (
                            <div className="flex my-2 gap-2">
                                <button
                                    className="btn px-4 bg-red-500 rounded"
                                    onClick={() => handleDelete(_id)}
                                >
                                    <AiOutlineDelete />
                                </button>
                                <button
                                    className="btn px-4 bg-yellow-500 rounded"
                                    onClick={() => navigate(`/edit/${_id}`)}
                                >
                                    <AiOutlineEdit />
                                </button>
                            </div>
                        ) : (
                            <button
                                className="lg:text-3xl bg-green-300 py-2 lg:py-3 px-4 lg:px-6 rounded-lg hover:bg-green-400 transition-all focus:outline-none focus:border-none"
                                onClick={() =>
                                    navigate(`/create-order/${productId}`)
                                }
                            >
                                Buy Now
                            </button>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
}

export default ProductDetail;
